.rvtoc

 LDA #17                ; ???
 STA track
 LDA #0
 STA sector

; ******************************************************************************
;
;       Name: rsect
;       Type: Subroutine
;   Category: Save and load
;    Summary: Read sector from disk into buffer
;
; ******************************************************************************

 \REM DOS_RW2

.rsect

 CLC                    ; ???
 BCC wsect2 ; always

; ******************************************************************************
;
;       Name: wsect
;       Type: Subroutine
;   Category: Save and load
;    Summary: Write sector from buffer to disk
;
; ------------------------------------------------------------------------------
;
; Other entry points:
;
;   wsect2              ???
;
; ******************************************************************************

.wsect

 SEC                    ; ???

.wsect2

 \ drive = 1, track = ?track, sector = ?sector
 PHP
 LDA #$60
 STA slot16
 LDA #2
 STA recals ; init max number of arm recalibrations
 LDA #4
 STA seeks ; init max number of seeks
 LDA #$D8
 STA mtimeh ; init motor on time hi
 LDX slot16 ; get slot number*16 of controller card
 LDA Q7L,X ; prepare latch for input
 LDA Q6L,X ; strobe data latch for I/O
 LDY #8

.rwts2

 LDA Q6L,X ; read data
 PHA ; short delay
 PLA
 PHA
 PLA
 CMP $100
 CMP Q6L,X
 BNE rwts3 ; branch if data latch changed ie. disc is spinning
 DEY
 BNE rwts2

.rwts3

 PHP ; save result - Z = 0 = disc is spinning, Z = 1 = disc not spinning
 LDA mtron,X ; turn motor on - if disc was not spinning
 LDA drv1en,X ; enable drive 1
 PLP
 PHP
 BNE rwts5 ; branch if disc is spinning
 LDY #7

.rwts4

 JSR armwat ; wait for capacitor to discharge
 DEY
 BNE rwts4
 LDX slot16

.rwts5

 LDA track
 JSR seek
 PLP
 BNE trytrk ; branch if disc is spinning
 LDY mtimeh
 BPL trytrk ; branch if motor reached correct speed

.rwts6

 LDY #18 ; delay for motor to reach correct speed

.rwts7

 DEY
 BNE rwts7
 INC mtimel
 BNE rwts6
 INC mtimeh
 BNE rwts6

.trytrk

 PLP ; get read/write status
 PHP
 BCC trytr2 ; branch if read sector
 JSR prenib ; convert sector to 6 bit 'nibbles'

.trytr2

 LDY #48
 STY ztemp2

.trytr3

 LDX slot16
 JSR rdaddr ; find track address
 BCC rdrght ; branch if no error

.trytr4

 DEC ztemp2
 BPL trytr3 ; branch if not last try

.trytr5

 DEC recals
 BEQ drverr ; branch if last try
 LDA #4
 STA seeks
 LDA #$60
 STA curtrk
 LDA #0
 JSR seek ; reset head

.trytr6

 LDA track
 JSR seek ; seek to desired track
 JMP trytr2

.rdrght

 LDY idfld+2
 CPY track
 BEQ rttrk ; branch if track does match track id
 DEC seeks
 BNE trytr6
 BEQ trytr5 ; always

.prterr

 \ disc write protected
 LDA #1
 BPL drver2_copy

{
.drverr     ; Removed as it isn't used and clashes with drverr below
}

 \ disc I/O error
 LDA #4 ; I/O error

.drver2

 LDX stkptr
 TXS
 LDX slot16
 LDY mtroff,X ; turn motor off
 SEC ; signify error has occured
 RTS

.rttrk

 LDY sector
 LDA scttab,Y
 CMP idfld+1
 BNE trytr4 ; branch if sector doesn't match sector id
 PLP
 BCS rttrk2 ; branch if write sector
 JSR read
 PHP
 BCS trytr4 ; branch if read error
 PLP
 JSR pstnib ; convert sector to 8 bit bytes
 JMP rttrk3

.rttrk2

 JSR write
 BCC rttrk3 ; branch if no error
 LDA #1

 BPL drver2

.drverr

 \ disc I/O error
 LDA #4 ; I/O error

;.drver2

.drver2_copy            ; Added as drver2 is repeated

 LDX stkptr
 TXS
 SEC
 BCS rttrk4

.rttrk3

 LDA #0
 CLC

.rttrk4

 LDX slot16
 LDY mtroff,X ; turn motor off
 RTS ; C = 0 = no error, C = 1 = error, A = error code

; ******************************************************************************
;
;       Name: read
;       Type: Subroutine
;   Category: Save and load
;    Summary: Read sector
;
; ******************************************************************************

.read

 LDY #32                ; ???

.read2

 DEY
 BEQ readE

.read3

 LDA Q6L,X
 BPL read3

.read4

 EOR #$D5
 BNE read2
 NOP

.read5

 LDA Q6L,X
 BPL read5
 CMP #$AA
 BNE read4
 LDY #$56

.read6

 LDA Q6L,X
 BPL read6
 CMP #$AD
 BNE read4
 LDA #0

.read7

 DEY
 STY ztemp0

.read8

 LDY Q6L,X
 BPL read8
 EOR rtable-$96,Y
 LDY ztemp0
 STA buffr2+256,Y
 BNE read7

.read9

 STY ztemp0

.readA

 LDY Q6L,X
 BPL readA
 EOR rtable-$96,Y
 LDY ztemp0
 STA buffr2,Y
 INY
 BNE read9

.readB

 LDY Q6L,X
 BPL readB
 CMP rtable-$96,Y
 BNE readE

.readC

 LDA Q6L,X
 BPL readC
 CMP #$DE
 BNE readE
 NOP

.readD

 LDA Q6L,X
 BPL readD
 CMP #$AA
 BEQ readF

.readE

 SEC
 RTS

.readF

 CLC
 RTS

; ******************************************************************************
;
;       Name: write
;       Type: Subroutine
;   Category: Save and load
;    Summary: Write sector
;
; ******************************************************************************

.write

 SEC                    ; ???
 STX ztemp1
 LDA Q6H,X
 LDA Q7L,X
 BMI write6
 LDA buffr2+256
 STA ztemp0
 LDA #$FF
 STA Q7H,X
 ORA Q6L,X
 PHA
 PLA
 NOP
 LDY #4

.write2

 PHA
 PLA
 JSR wbyte2
 DEY
 BNE write2
 LDA #$D5
 JSR wbyte
 LDA #$AA
 JSR wbyte
 LDA #$AD
 JSR wbyte
 TYA
 LDY #$56
 BNE write4

.write3

 LDA buffr2+256,Y

.write4

 EOR buffr2+255,Y
 TAX
 LDA wtable,X
 LDX ztemp1
 STA Q6H,X
 LDA Q6L,X
 DEY
 BNE write3
 LDA ztemp0
 NOP

.write5

 EOR buffr2,Y
 TAX
 LDA wtable,X
 LDX slot16
 STA Q6H,X
 LDA Q6L,X
 LDA buffr2,Y
 INY
 BNE write5
 TAX
 LDA wtable,X
 LDX ztemp1
 JSR wbyte3
 LDA #$DE
 JSR wbyte
 LDA #$AA
 JSR wbyte
 LDA #$EB
 JSR wbyte
 LDA #$FF
 JSR wbyte
 LDA Q7L,X

.write6

 LDA Q6L,X
 RTS

; ******************************************************************************
;
;       Name: rdaddr
;       Type: Subroutine
;   Category: Save and load
;    Summary: Read track address field
;
; ******************************************************************************

 \REM DOS_RW3

.rdaddr

 \ read track address field
 LDY #$FC
 STY ztemp0

.rdadr2

 INY
 BNE rdadr3
 INC ztemp0
 BEQ rdadrD

.rdadr3

 LDA Q6L,X
 BPL rdadr3

.rdadr4

 CMP #$D5
 BNE rdadr2
 NOP

.rdadr5

 LDA Q6L,X
 BPL rdadr5
 CMP #$AA
 BNE rdadr4
 LDY #3

.rdadr6

 LDA Q6L,X
 BPL rdadr6
 CMP #$96
 BNE rdadr4
 LDA #0

.rdadr7

 STA ztemp1

.rdadr8

 LDA Q6L,X
 BPL rdadr8
 ROL A
 STA ztemp0

.rdadr9

 LDA Q6L,X
 BPL rdadr9
 AND ztemp0
 STA idfld,Y
 EOR ztemp1
 DEY
 BPL rdadr7
 TAY
 BNE rdadrD

.rdadrA

 LDA Q6L,X
 BPL rdadrA
 CMP #$DE
 BNE rdadrD
 NOP

.rdadrB

 LDA Q6L,X
 BPL rdadrB
 CMP #$AA
 BNE rdadrD

.rdadrC

 CLC
 RTS

.rdadrD

 SEC
 RTS

; ******************************************************************************
;
;       Name: seek
;       Type: Subroutine
;   Category: Save and load
;    Summary: ???
;
; ******************************************************************************

.seek

 \ A = desired track
 STX ztemp0             ; ???
 ASL A
 CMP curtrk
 BEQ step3 ; branch if head already over desired track
 STA ztemp1 ; save desired track*2
 LDA #0
 STA ztemp2

.seek2

 LDA curtrk
 STA ztemp3
 SEC
 SBC ztemp1
 BEQ seek7 ; branch if reached desired track
 BCS seek3 ; branch if step back
 EOR #$FF
 INC curtrk ; track = track+1
 BCC seek4 ; always

.seek3

 ADC #$FE
 DEC curtrk ; track = track-1

.seek4

 CMP ztemp2
 BCC seek5
 LDA ztemp2

.seek5

 CMP #12
 BCS seek6
 TAY

.seek6

 SEC
 JSR step
 LDA armtab,Y
 JSR armwat
 LDA ztemp3
 CLC
 JSR step2
 LDA armtb2,Y
 JSR armwat
 INC ztemp2
 BNE seek2 ; always

.seek7

 JSR armwat
 CLC

; ******************************************************************************
;
;       Name: step
;       Type: Subroutine
;   Category: Save and load
;    Summary: Step drive head
;
; ------------------------------------------------------------------------------
;
; Other entry points:
;
;   step2               ???
;
;   step3               Contains an RTS
;
; ******************************************************************************

.step

 LDA curtrk             ; ???

.step2

 AND #3
 ROL A
 ORA ztemp0
 TAX
 LDA phsoff,X ; stepper motor phase 0-3 on/off
 LDX ztemp0

.step3

 RTS

