; ******************************************************************************
;
;       Name: prenib
;       Type: Subroutine
;   Category: Save and load
;    Summary: Convert 256*8 bit bytes to 342*6 bit 'nibbles'
;
; ******************************************************************************

.prenib

 LDX #0                 ; ???
 LDY #2

.prenb2

 DEY
 LDA buffer,Y
 LSR A
 ROL buffr2+256,X
 LSR A
 ROL buffr2+256,X
 STA buffr2,Y
 INX
 CPX #$56
 BCC prenb2
 LDX #0
 TYA
 BNE prenb2
 LDX #$55

.prenb3

 LDA buffr2+256,X
 AND #$3F
 STA buffr2+256,X
 DEX
 BPL prenb3
 RTS

; ******************************************************************************
;
;       Name: pstnib
;       Type: Subroutine
;   Category: Save and load
;    Summary: Convert 342*6 bit 'nibbles' to 256*8 bit bytes
;
; ******************************************************************************

.pstnib

 LDY #0                 ; ???

.pstnb2

 LDX #$56

.pstnb3

 DEX
 BMI pstnb2
 LDA buffr2,Y
 LSR buffr2+256,X
 ROL A
 LSR buffr2+256,X
 ROL A
 STA buffer,Y
 INY
 BNE pstnb3
 RTS

