.letter

 \plot character A at X,YC*8
 LDY #HI(FONT)-1
 ASL A
 ASL A
 BCC P%+4
 LDY #HI(FONT)+1
 ASL A
 BCC RR9
 INY

.RR9

;CLC
;ADC #LO(FONT)
 STA P
;BCC P%+3
;INY
 STY P+1

.letter2

 LDY YC                 ; Set the low byte of SC(1 0) to the YC-th entry from
 LDA SCTBL,Y            ; SCTBL, which contains the low byte of the address of
 STA SC                 ; the start of character row YC in screen memory

 LDA SCTBH,Y
 STA SC+1
 LDY SCTBX1,X
 STY P+2
 LDY SCTBX2,X
 STY T1
 LDY #0

.RRL1

 LDA #0
 STA T3
 LDA (P),Y
 LDX P+2

.RRL2

 CMP #128
 ROL T3
 DEX
 BMI RR8
 ASL A
 JMP RRL2

.RR8

 AND #127
 CLC
 STY T2
 LDY T1
 EOR (SC),Y
 STA (SC),Y
 INY
 LDA T3
 EOR (SC),Y
 STA (SC),Y
 LDY T2
 LDA SC+1
 ADC #4
 STA SC+1
 INY
 CPY #8
 BNE RRL1
 RTS

