 CLC
 LDX INWK+2
 BPL SC2
 EOR #$FF
 ADC #1
 CLC

.SC2

 ADC #125
 AND #$FE
 STA X1
 TAX
 DEX
 DEX
 LDA INWK+7
 LSR A
 LSR A
 CLC
 LDY INWK+8
 BPL SC3
 EOR #$FF
 SEC

.SC3

 ADC #91 ;83
 EOR #$FF
 STA Y2
 LDA INWK+4
 LSR A
 CLC
 LDY INWK+5
 BMI SCD6
 EOR #$FF
 SEC

.SCD6

 ADC Y2
;BPL ld246
 CMP #146 ;194
 BCS P%+4
 LDA #146
 CMP #191 ;199
 BCC P%+4

.ld246

 LDA #190 ;198
 JSR CPIX
 JMP VLOIN

