; ******************************************************************************
;
;       Name: F%
;       Type: Variable
;   Category: Utility routines
;    Summary: Denotes the end of the main game code, from ELITE A to ELITE K
;
; ******************************************************************************

.F%

 SKIP 0

IF _IB_DISK

 EQUB $83, $6F          ; These bytes appear to be unused
 EQUB $63, $6F
 EQUB $75

ENDIF

; ******************************************************************************
;
; Save ELTK.bin
;
; ******************************************************************************

 PRINT "ELITE K"
 PRINT "Assembled at ", ~CODE_K%
 PRINT "Ends at ", ~P%
 PRINT "Code size is ", ~(P% - CODE_K%)
 PRINT "Execute at ", ~LOAD%
 PRINT "Reload at ", ~LOAD_K%

 PRINT "S.ELTK ", ~CODE_K%, " ", ~P%, " ", ~LOAD%, " ", ~LOAD_J%
 SAVE "3-assembled-output/ELTK.bin", CODE_K%, P%, LOAD%

 PRINT "Addresses for the scramble routines in elite-checksum.py"
 PRINT "B% = ", ~CODE%
 PRINT "G% = ", ~G%
 PRINT "NA2% = ", ~NA2%
